commits.content:
.course {
    font-size: 14px;
    opacity: 0;
    display: flex;
    align-items: flex-end;
    justify-content: space-between;
    margin: 20px 0px;
    border-radius: 15px;
    background-color: white;
    box-shadow: 4px 4px 5px  darken($colour-gray,12);
    height: 75px;
    overflow: hidden;
    @include anime;

}    
.course:hover .dish{
    width: 100px; 
} 
.course:hover .validate {
    width: 100px;
    opacity: 1;
}
.course .fa-check-circle {
    color: white;
    font-size: 30px;
    transition: transform 1.5s;

    :hover .fa-check-circle {
    transform: rotate(360deg);
    }
}
.dish {
    transition: width 1s;
    width: 350px;
}
.prix{
    width: 50px;
    text-align: right;
    //z-index: 5;
    padding-bottom: 15px;
    padding-right: 20px;
}
.validate {
    display: flex;
    justify-content: center;
        align-items: center;
        border-top-right-radius: 15px;
        border-bottom-right-radius: 15px;
        background-color:$colour-tertiary;
        width: 0px;
        opacity: 0;
        height: 100%;
        margin-left: 2px;
        transition: width 1s, opacity 1s;
}
