commits.content:
 i {
    //font-family: Font Awesome 6 Free;
    font-size: 22px;

    &.fa-mobile-screen{

         font-size: 24px;
         padding-right: 0px;
         //margin-left: 10px;



     }


&.fa-circle-1{
        color: blue($color: #000000);
        position: relative;

        margin: 0;
    }
}
 .description{
    position: relative;
    padding-bottom: 15px;

 }

 .description i {
    font-size: 22px;
    position: absolute;
    top: 30%;
    right: 1rem;

 }
 .description .far{
    background : none;
    transition: all 350ms ease-in-out;
 }
:hover .far{
    opacity: 0;
    transform: scale(0);
}
.description .fa {
    opacity: 0;
    transform: scale(0);
    background: linear-gradient($colour-secondary,$colour-primary);
    -webkit-background-clip: text;
    -moz-background-clip: text;
    background-clip: text;
    -webkit-text-fill-color: transparent;
}
 :hover .description .fa{
    opacity: 1;
    transform: scale(1);
}
