commits.content:
@keyframes slidein {
    from { transform: translate(0,10px);opacity:0 }
    to   { transform: translate(0,0px);opacity:1 }
  }

  .container>.card {
    margin-bottom: 10px;
  }
  .container>.card:nth-child(1) {
    margin-bottom: 10px;
    animation: slidein 1s ease-in;
  }
  .container>.card:nth-child(2) {
    margin-bottom: 10px;
    animation: slidein 1s ease-in 0.33s;
  }
  .container>.card:nth-child(3) {
    margin-bottom: 10px;
    animation: slidein 1s ease-in 0.66s;
  }
  .card {
    height: 80px;
    width: 300px;
    padding-left: 10px;
    border: 1px solid black;
    border-radius: 15px;
    display: flex;
    align-items: center;
    overflow: hidden;
  }

  .part1 {
    width: 250px;
    transition: width 1s;
  }

  .part1>h3 {
    overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
  }

  .part1>p {
    overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
  }

  .part2 {
    width: 100px;
    text-align: center;
    z-index: 5;
  }

  .snipped {
    display: flex;
    border-top-right-radius: 15px;
    border-bottom-right-radius: 15px;
    background-color: green;
    width: 0px;
    opacity: 0;
    height: 100%;
    justify-content: center;
    align-items: center;
    transition: width 5s, opacity 1s;
  }

  .fa-check-circle {
    color: white;
    font-size: 20px;
    transition: transform 1.5s;
  }

  .card:hover .snipped {
    width: 100px;
    opacity: 1;
  }

  .card:hover .fa-check-circle {
    transform: rotate(360deg);
  }

  .card:hover .part1 {
    width: 180px;
  }
