@import url("https://fonts.googleapis.com/css2?family=Roboto:wght@400;500;700;900&display=swap");
@import url("https://fonts.googleapis.com/css2?family=Shrikhand&display=swap");
@import url("https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.1.2/css/all.css");
body {
  font-family: Roboto, sans-serif;
  font-size: 18px;
  margin: 0;
  padding: 0;
}

h1 {
  font-family: Shrikhand, sans-serif;
  font-size: 40px;
  font-weight: 700;
  line-height: 1.1;
  text-align: center;
}

h2, h3, p {
  padding-left: 20px;
}

h4 {
  font-family: Shrikhand, sans-serif;
  font-size: 30px;
}

i {
  font-family: Font Awesome 6 Free;
  font-size: 20px;
  margin-right: 15px;
  width: 25px;
}

ul {
  list-style-type: none;
  text-decoration: none;
}

a {
  text-decoration: none;
  color: #000;
}

span {
  font-family: Roboto, sans-serif;
  font-size: 50px;
}

h5 {
  text-transform: uppercase;
}

main .rechercher {
  background-color: #CCC;
  height: 70px;
  box-shadow: 5px 10px 15px 2px #b3b3b3;
}

.Introduction, .Liste_restaurants {
  background-color: #f2f2f2;
  padding-bottom: 30px;
}

.Introduction {
  background-color: #f2f2f2;
  text-align: center;
  display: block;
  margin-left: auto;
  margin-right: auto;
}

.fonctionnement {
  justify-content: space-evenly;
  padding-bottom: 30px;
}

.Introduction, .Liste_restaurants {
  background-color: #f2f2f2;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
}

footer {
  background-color: #000000;
}
footer h4 {
  margin: 0;
  padding: 30px 0px 0px 45px;
  color: #FFFFFF;
}
footer ul {
  padding-top: 0px;
  padding-bottom: 20px;
  margin: 0%;
}
footer a {
  text-decoration: none;
  color: #FFFFFF;
}

.card_restaurant {
  background-color: #FFFFFF;
  margin: 2% auto;
  position: relative;
  width: 90%;
  box-shadow: rgba(0, 0, 0, 0.35) 0px 5px 15px;
  border-radius: 15px;
  padding: 10px;
}
.card_restaurant h3, .card_restaurant p {
  margin: 0;
}
.card_restaurant img {
  width: 98%;
  height: 200px;
  object-fit: cover;
  border-radius: 15px;
  display: block;
  margin-left: auto;
  margin-right: auto;
  padding: 10px;
}
.card_restaurant .green_card {
  background-color: #99E2D0;
  position: absolute;
  right: 10%;
  top: 15%;
  padding: 10px;
}
.card_restaurant i {
  float: right;
}

button {
  background-color: rgb(220, 76, 14);
  border-radius: 50%;
  display: inline-block;
  margin: auto;
  margin-top: 0px;
  color: green;
}
button.pink {
  background: linear-gradient(to bottom right, #FF79DA, 45%, #9356DC);
  color: white;
  font-weight: bold;
  height: 45px;
  width: 100px;
  border-radius: 20px;
  position: relative;
  box-shadow: 2px 3px 5px 4px #b3b3b3;
  z-index: 0;
}
button .grey {
  border: none;
  display: block;
  margin: auto;
  margin-top: 50px;
  background: lighten #CCC;
  color: #000000;
  font-weight: bold;
  height: 80px;
  border-radius: 10px;
  box-shadow: 2px 3px 5px 4px #b3b3b3;
  width: 187px;
}
button .lightpink {
  border: none;
  display: block;
  margin: auto;
  margin-top: 50px;
  background: lighten #CCC;
  color: #000000;
  font-weight: bold;
  height: 80px;
  border-radius: 10px;
  box-shadow: 2px 3px 5px 4px lighten #9356DC;
  width: 260px;
  margin-bottom: 0px;
}/*# sourceMappingURL=main.css.map */
